import Comp_prac from './compo/Comp_prac';
import Wrapper from './compo/Wrapper';
import Hello from './compo/Hello';
const style = {
  color: 'red',
  backgroundColor: 'green',
  fontSize: '20px',
  padding: '1rem' //다른 단위 사용시 문자열로 설정
}
function Header(tt) {
  return (
    <header>
      {/* 주석 */}
      <h1><a href="/">{tt.title}</a></h1>
    </header>
  )
}
function Nav(q) {
  const lis = []
  for (let i = 0; i < q.topics.length; i++) {
    let t = q.topics[i];
    lis.push(<li key={t.id}><a href={`/read/${t.id}`}></a>{t.title}{t.body}</li>);
  }
  return (
    <nav style={style}>
      <ol>
        {lis}
      </ol>
    </nav>
  )
}
function Article(a) {
  return (
    <article>
      <h2>{a.title}</h2>
      {a.body}
    </article>
  )
}

