import './App.css';
import Student from './compo/Student';
import Comp01 from './compo/Comp01';
import Comp02 from './compo/Comp02';
import Comp_prac from './compo/Comp_prac';
import Wrapper from './compo/Wrapper';
import Hello from './compo/Hello';
const style = {
  color: 'red',
  backgroundColor: 'green',
  fontSize: '20px',
  padding: '1rem' //다른 단위 사용시 문자열로 설정
}


function Header(tt) {
  return (
    <header>
      {/* 주석 */}
      <><a href="/" onClick={
        function (event) {
          event.preventDefault();
          tt.onChangeMode();
        }
      }>{tt.title}</a></>
    </header>
  )
}
function Nav(q) {
  const lis = []
  for (let i = 0; i < q.topics.length; i++) {
    let t = q.topics[i];
    lis.push(<li key={t.id}>
      <a href={`/read/${t.id}`} onClick={
        event => {
          event.preventDefault();
          q.onChangeMode(event.target.id);
        }
      } id={t.id}>{t.title}{t.body}</a>
    </li>);
  }
  return (
    <nav style={style}>
      <ol>
        {lis}
      </ol>
    </nav>
  )
}
function Article(a) {
  return (
    <article>
      <h2>{a.title}</h2>
      {a.body}
    </article>
  )
}

function App() {
  const mode = 'WELCOME'
  const name = 'react'
  const topics = [
    { id: 1, title: 'html', body: ' is .......' },
    { id: 2, title: 'css', body: ' is .......' },
    { id: 3, title: 'javascript', body: ' is .......' }
  ]
  let content = null;
  if (mode === 'WELCOME') {
    content = <Article title='WELCOME' body={topics[0].title} />
  }else if(mode ==='READ'){
    content = <Article title='WELCOME' body={"Hello, read"}/>
  }
  return (
    <div className="App">
      {/* Hello, Wordl! */}
      <Header title='11' onChangeMode={function () {
        alert("header")
      }
      } />
      <Nav topics={topics} onChangeMode={(id) =>
        alert(id)} />{name}
      <Article title='hi' body='bbbb,web' />
      {/* <Article title='aa' body='ccc' />
      <div style={style}>
        {name}
      </div>
      <div className="gray-box"></div>
      <Student />  */}
      {/* <Comp01/>
      <Comp02/> */}
      {/* <Wrapper>
        <Hello color='green' name = 'React' favoriteNumber={100}/>
        <Hello color='blue' favoriteNumber={100222}/>
        <Hello color='green' name = {123}/>
      </Wrapper> */}
      {/* <Comp_prac/> */}
    </div>
  );
}

export default App;
