import {Component} from 'react';

class Cnt extends Component {
  constructor(props){
    super(props);
    this.state ={
      number:0
    };
  }

  render(){
    const {number} = this.state;
    return (
      <div>
        <h1>{number}</h1>
        <button onClick={function(){
          this.state({number: num+1});
        }}>+1</button>
        <button onClick={function(){
          this.state(() => {number => number+1});
        }}>-1</button>
      </div>
    )
  }
}

export default Cnt1;
