import './App.css';
import Student from './compo/Student';
const style = {
  color: 'red',
  backgroundColor: 'green',
  fontSize: '20px',
  padding: '1rem' //다른 단위 사용시 문자열로 설정
}
function Header(tt) {
  return (
    <header>
      {/* 주석 */}
      <h1><a href="/">{tt.title}</a></h1>
    </header>
  )
}
function Nav(q) {
  const lis = [
    <li><a href="/read/1"></a>{q.body}</li>,
    <li><a href="/read/2"></a>{q.topics}</li>,
    <li><a href="/read/3"></a>{q.topics}</li>
  ]
  return (
    <nav style={style}>
      <ol>
      {lis}
      </ol>
    </nav>
  )
}
function Article(a) {
  return (
    <article>
      <h2>{a.title}</h2>
      {a.body}
    </article>
  )
}

function App() {
  const name = 'react'
  const topics = [
    { id: 1, title: 'html', body: 'html is .......' },
    { id: 2, title: 'css', body: 'css is .......' },
    { id: 3, title: 'javascript', body: 'javascript is .......' }
  ]
  return (
    <div className="App">
      Hello, Wordl!
      <Header title='11' />
      <Nav topics={topics} />{name}
      <Article title='hi' body='bbbb,web' />
      <Article title='aa' body='ccc' />
      <div style={style}>
        {name}
      </div>
      <div className="gray-box"></div>
      <Student />
    </div>
  );
}

export default App;
