import './App.css';
import Student from './compo/Student';
const style = {
  color : 'red',
  backgroundColor : 'green',
  fontSize : '20px',
  padding : '1rem' //다른 단위 사용시 문자열로 설정
}
function Header() {
  return (
    <header>
      {/* 주석 */}
      <h1><a href="/">web</a></h1>
    </header>
  )
}
function Nav() {
  return (
    <nav style={style}>
      <ol>
        <li><a href="/read/1"></a>html</li>
        <li><a href="/read/2"></a>css</li>
        <li><a href="/read/3"></a>js</li>
      </ol>
    </nav>
  )
}
function Article() {
  return (
    <article>
      <h2>Welcome</h2>
      Hello,web
    </article>
  )
}

function App() {
  const name = 'react'

  return (
    <div className="App">
      Hello, Wordl!
      <Header />
      <Nav />{name}
      <Article />
      <div style={style}>
        {name}
      </div>
      <div className="gray-box">
        {name}
      </div>
    </div>
  );
}

export default App;
